const accs=document.getElementsByClassName("accordion");for(i=0;i<accs.length;i++)accs[i].addEventListener("click",function(){this.classList.toggle("active");var t=this.nextElementSibling;t.style.maxHeight?t.style.maxHeight=null:t.style.maxHeight=t.scrollHeight+"px"});const upBtn=document.getElementById("up");window.addEventListener("scroll",t=>{100<window.scrollY?upBtn.classList.add("active"):upBtn.classList.remove("active")});